sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
		return;
	}

	var ga = new GlideAjax("GetRequesterDetails");
	ga.addParam("sysparm_name" , "getData");
	ga.addParam("sysparm_userId" , g_form.getValue("u_requested_for"));
	ga.getXML(populateData);


	function populateData(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var dataParsed = JSON.parse(answer);

		g_form.setValue("u_requested_for_manager",dataParsed.manager);
		g_form.setValue("u_mobile_phone",dataParsed.phone);

	}

}
