var AcmeaddGlideListElement = Class.create();
AcmeaddGlideListElement.prototype = {
    initialize: function() {
    },

	addGlideListElement : function (grField ,id){
		if(JSUtil.notNil(grField))
			var idList = grField.split(',');
			else
				var idList =[];

		//Determine if the id passed is allready in the list
		var found = false ;
		for(var i=0; i<idList.length ;i++){
			if(id ==idList [i]){
				found = true;
				break;
			}
		}

		//If not on the list then add it 

		if(!found)
			idList.push(id);

		//Return the comma separated list of array of ids

		return idList.join(',');

		//Reusing storing of variables


		var currentUser = gs.getUserID();
		var isOwner = (currentUser == current.assigned_to);
		var isCoordinator = (currentUser == current.u_coordinator);
		var isCaller = (currentUser ==current.caller_id);

		if(isOwner || isCoordinator ||isCaller) {
			//Do something
		}
	},




    type: 'AcmeaddGlideListElement'
};
